import React, { useState } from "react";

function CreateArea() {
  const [noteDetails, setNoteDetails] = useState({title : "", content : ""})

  function handleOnChange(event){
    const {name, value} = event.target;

    setNoteDetails(prevNote=>{
      return{
      ...prevNote,
      [name] : value
  }})
  }

  return (
    <div>
      <form>
        <input name="title" onChange={handleOnChange} value={noteDetails.noteTitle} placeholder="Title" />
        <textarea name="content" onChange={handleOnChange} value={noteDetails.noteContent} placeholder="Take a note..." rows="3"/>
        {/* <button onClick={()=>{
          props.onAdd(noteDetails);
          setNoteDetails({noteTitle : "", noteContent : ""})
        }}>Add</button> */}
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
