import React, { useState } from "react";

function CreateArea(props) {
  const [noteDetails, setNoteDetails] = useState({noteTitle : "", noteContent : ""})

  function handleOnChange(event){
    const {name, value} = event.target;
    setNoteDetails(prevNote=>{
      return{
      ...prevNote,
      [name] : value
  }})
  }

  return (
    <div>
      <form>
        <input onChange={handleOnChange} name="title" placeholder="Title" value={noteDetails.noteTitle}/>
        <textarea onChange={handleOnChange} name="content" placeholder="Take a note..." rows="3" value={noteDetails.noteContent}/>
        <button onClick={()=>{
          props.onAdd(noteDetails);
          setNoteDetails({noteTitle : "", noteContent : ""})
        }}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
